mapper.select:
SELECT PRODUCT.ID , NAME , PRICE , CATEGORY_CODE , CATEGORIES.CODE_NAME AS CATEGORY_NAME , HIT , STOCKS , IMAGE AS THUMBNAIL , INSERT_DATE FROM PRODUCT LEFT JOIN COMMON_CODE CATEGORIES ON CATEGORIES.CODE_GROUP1 = 'CATEGORY' AND CATEGORIES.CODE_GROUP2 = 'CATEGORY' AND CATEGORIES.CODE = CATEGORY_CODE LEFT JOIN PRODUCT_IMAGE IMAGE ON IMAGE.ID = PRODUCT.ID AND IMAGE.SEQ = 1 WHERE 1 = 1 AND CATEGORY_CODE = #{P_CATEGORY} ORDER BY
HIT DESC PRICE ASC PRICE DESC INSERT_DATE DESC PRODUCT.ID ASC
LIMIT #{P_START}, #{P_VIEW_NUM}

mapper.update:
UPDATE PRODUCT
SET    HIT = HIT + 1
WHERE  ID = #{P_PRODUCT_ID}
