mapper.select:
SELECT PRODUCT.ID , NAME , PRICE , CATEGORY_CODE , CATEGORIES.CODE_NAME AS CATEGORY_NAME , HIT , STOCK , IMAGE AS THUMBNAIL , INSERT_DATE FROM PRODUCT LEFT JOIN COMMON_CODE CATEGORIES ON CATEGORIES.CODE_GROUP1 = 'CATEGORY' AND CATEGORIES.CODE_GROUP2 = 'CATEGORY' AND CATEGORIES.CODE = CATEGORY_CODE LEFT JOIN PRODUCT_IMAGE IMAGE ON IMAGE.ID = PRODUCT.ID AND IMAGE.SEQ = 1 WHERE 1 = 1 AND CATEGORY_CODE = #{P_CATEGORY} ORDER BY
HIT DESC PRICE ASC PRICE DESC INSERT_DATE DESC PRODUCT.ID ASC
LIMIT #{P_START}, #{P_VIEW_NUM}

mapper.update:
UPDATE PRODUCT
SET    HIT = HIT + 1
WHERE  ID = #{P_PRODUCT_ID}

mapper.insert:

    SELECT LAST_INSERT_ID()


INSERT INTO PRODUCT (
    NAME
    , PRICE
    , CATEGORY_CODE
    , SIZEINFO
    , CONTENT
    , STOCK
) VALUES (
    #{P_NAME}
    , #{P_PRICE}
    , #{P_CATEGORY_CODE}
    , #{P_SIZEINFO, jdbcType=VARCHAR}
    , #{P_CONTENT, jdbcType=VARCHAR}
    , #{P_STOCK}
)

INSERT INTO PRODUCT_IMAGE (
    ID
    , SEQ
    , IMAGE
) VALUES (
    #{P_ID}
    , #{P_SEQ}
    , #{P_IMAGE}
)

INSERT INTO PRODUCT_SIZE (
    ID
    , CATEGORY_CODE
    , SIZE_CODE
) VALUES (
    #{P_ID}
    , #{P_CATEGORY_CODE}
    , #{P_SIZE}
)
